mapper.update:
UPDATE "TOGETHER" SET BOARD_TITLE = #{boardTitle}, BOARD_CONTENT = #{boardContent}, MAX_PEOPLE = #{maxPeople}, METTING_DATE = TO_DATE(#{mettingDate},'YYYY-MM-DD"T"HH24:MI')
,BOARD_IMG = NULL
,BOARD_IMG = #{boardImg}
WHERE BOARD_NO = #{boardNo}

 	UPDATE "TOGETHER" SET
		BOARD_DEL_FL = 'Y'
 	WHERE BOARD_NO = #{boardNo}
 	AND MEMBER_NO = #{memberNo}
